import { useRef, useState } from "react";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import './Auth.css';
const Auth = () => {
    const emailInputRef = useRef();
    const passwordInputRef = useRef();
    const [isLogin, setIsLogin] = useState(true);

    const switchAuthModeHandler = () => {
        setIsLogin((prevState) => !prevState);
    };



    return (<div>
        <h2>Welcome!!!!</h2>

        <Container fluid >
            <br></br>
            <br></br>
            <br></br>
            <br></br>

            <Row>
                <Col xs={1}></Col>
                <Col xs={3} className="wrapper"> <h2>{isLogin ? 'Login' : 'Sign Up'}</h2>
                    <form>
                        <div className="inputs">
                           
                            <input type='email' id='email' className="cusbox"  placeholder="     Email" ref={emailInputRef} required />
                        </div>
                        <div className="inputs">
                            
                            <input type='password' id='password' className="cusbox" placeholder="     Password" ref={passwordInputRef} required />
                        </div>

                        <br></br>
                        
                        <hr style={{width:"80%" , borderWidth:"3px", borderColor:"black"}}></hr>
                        <br></br>
                        <div>
                            <button>Login</button>
                            <button
                                type='button'

                               
                            >
                                {isLogin ? 'Create new account' : 'Login with existing account'}
                            </button>
                           
                        </div>
                    </form>

                    <div>
                        <p>Or Login With</p>
                        <button>LOgin via gsyc</button>
                    </div>
                </Col>
               
            </Row>

        </Container>




    </div>)
};

export default Auth;
